bool ispow_2(ll num)
{ 
   return  (num != 0 && ((num & (num - 1)) == 0) );
}
bool is_sqmodp(int v,int p)
{  
   if(v<0)
     v+=p;
   bool f=0;
   for(ll i=0;i*i <=1000000; i++)
    { if((i*i)%p==v)
       {f=1;break;}
     }
   return f;
}



