ll hmd( )
{
    h[0][0]=1;
    // if N>1; build hadamard with previous hadamard
    for (int n=1;n<N;n*=2)
   {
    for (int j=0;j<n;j++)
    {
        for (int i=0;i<n;i++)
        {
            // place previous hadamard in top right corner
            h[i][j+n] = h[i][j];
            // top left corner
            h[i+n][j] = h[i][j];
            // bottom right corner and invert values
            if (h[j][i] == -1)
            h[j+n][i+n]=1;
            else
            h[j+n][i+n]=-1;
        }
    }
   }
}
